package io.whatsapp.clonewhatsapp;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.EditText;import io.whatsapp.clonewhatsapp.model.User;/** * Created by rafaelcarvalho on 18/12/2017. */public class CadastroActivity extends AppCompatActivity {    private EditText nome, email, senha;    private Button btnCadastrar;    private User user;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_cadastro);        nome = (EditText) findViewById(R.id.edit_nome);        email = (EditText) findViewById(R.id.edit_email);        senha = (EditText) findViewById(R.id.edit_password);        btnCadastrar = (Button) findViewById(R.id.btn_cadastrar);        btnCadastrar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                user = new User();                user.setNome( nome.getText().toString() );                user.setEmail( email.getText().toString() );                user.setSenha( senha.getText().toString() );            }        });    }    public void cadastrarUser(){    }}