package io.whatsapp.clonewhatsapp;import android.content.Intent;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;import io.whatsapp.clonewhatsapp.firebase.ConfigFireBase;import io.whatsapp.clonewhatsapp.model.Usuario;/** * Created by rafaelcarvalho on 19/12/2017. */public class CadastroActivity extends AppCompatActivity {    private EditText nome;    private EditText email;    private EditText senha;    private Button btn_cadastrar;    private Usuario usuario;    private FirebaseAuth auth;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_cadastro);        nome = (EditText) findViewById(R.id.edit_nome);        email = (EditText) findViewById(R.id.edit_email);        senha = (EditText) findViewById(R.id.edit_email);        btn_cadastrar = (Button) findViewById(R.id.btn_cadastrar);        btn_cadastrar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                usuario = new Usuario();                usuario.setNome( nome.getText().toString() );                usuario.setEmail( email.getText().toString() );                usuario.setSenha( senha.getText().toString() );                cadastrarUsuario();            }        });    }    private void cadastrarUsuario(){        auth = ConfigFireBase.getFirebaseAuth();        auth.createUserWithEmailAndPassword(                usuario.getEmail(),                usuario.getSenha()        ).addOnCompleteListener(CadastroActivity.this, new OnCompleteListener<AuthResult>() {            @Override            public void onComplete(@NonNull Task<AuthResult> task) {                if( task.isSuccessful() ){                    Toast.makeText(CadastroActivity.this, "Sucesso ao cadastrar usuário", Toast.LENGTH_LONG ).show();                    FirebaseUser usuarioFirebase = task.getResult().getUser();                    usuario.setId( usuarioFirebase.getUid() );                    usuario.salvar();                }else {                    Toast.makeText(CadastroActivity.this, "Erro ao cadastrar usuário: ", Toast.LENGTH_LONG ).show();                }                limparCampos(nome, email, senha);                Intent i = new Intent(CadastroActivity.this, LoginActivity.class);                startActivity(i);            }        });    }    private void limparCampos(EditText nome, EditText email, EditText senha){        nome.setText("");        email.setText("");        senha.setText("");    }}