package io.whatsapp.clonewhatsapp;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.ValueEventListener;import io.whatsapp.clonewhatsapp.base64.Codificacao;import io.whatsapp.clonewhatsapp.firebase.ConfigFireBase;import io.whatsapp.clonewhatsapp.helpers.Preferencias;import io.whatsapp.clonewhatsapp.model.Usuario;public class LoginActivity extends AppCompatActivity {    private EditText email;    private EditText senha;    private Button btnLogar;    private TextView cadastro;    private Usuario usuario;    private FirebaseAuth autenticacao;    private DatabaseReference reference;    private ValueEventListener valueEventListenerUsuario;    private String idUsuarioLogado;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        verificandoUsuarioLogado();        email = (EditText) findViewById(R.id.edit_login_email);        senha = (EditText) findViewById(R.id.edit_login_senha);        btnLogar = (Button) findViewById(R.id.btn_logar);        cadastro = (TextView) findViewById(R.id.text_cadastro);        btnLogar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                usuario = new Usuario();                usuario.setEmail(email.getText().toString());                usuario.setSenha(senha.getText().toString());                login();            }        });        cadastro.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent i = new Intent(LoginActivity.this, CadastroActivity.class);                startActivity(i);            }        });    }    public void login(){       autenticacao = ConfigFireBase.getFirebaseAuth();       autenticacao.signInWithEmailAndPassword(               usuario.getEmail(),               usuario.getSenha()       ).addOnCompleteListener(new OnCompleteListener<AuthResult>() {           @Override           public void onComplete(@NonNull Task<AuthResult> task) {               if(task.isSuccessful()){                   idUsuarioLogado = Codificacao.codificacaoBase64( usuario.getEmail() );                   reference = ConfigFireBase.getFirebase()                           .child("usuarios")                           .child( idUsuarioLogado );                   valueEventListenerUsuario = new ValueEventListener() {                       @Override                       public void onDataChange(DataSnapshot dataSnapshot) {                           Usuario usuario = dataSnapshot.getValue( Usuario.class );                           Preferencias preferencias = new Preferencias(LoginActivity.this);                           preferencias.salvarDados( idUsuarioLogado, usuario.getNome() );                       }                       @Override                       public void onCancelled(DatabaseError databaseError) {                       }                   };                   reference.addListenerForSingleValueEvent( valueEventListenerUsuario );                   windowsMain();                   Toast.makeText(LoginActivity.this, "SUCESSO", Toast.LENGTH_LONG).show();               }else{                   Toast.makeText(LoginActivity.this, "ERROS", Toast.LENGTH_LONG).show();               }           }       });    }    private void windowsMain(){        Intent intent = new Intent(LoginActivity.this, MainActivity.class);        startActivity(intent);        finish();    }    private void verificandoUsuarioLogado(){        autenticacao = ConfigFireBase.getFirebaseAuth();        if( autenticacao.getCurrentUser() != null ){            windowsMain();        }    }}