package io.whatsapp.clonewhatsapp;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import com.google.firebase.auth.FirebaseAuth;import io.whatsapp.clonewhatsapp.firebase.ConfigFireBase;public class MainActivity extends AppCompatActivity {    private Button btnSair;    private FirebaseAuth auth;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        btnSair = (Button) findViewById(R.id.btn_sair);        btnSair.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                auth = ConfigFireBase.getFirebaseAuth();                auth.signOut();                windowsLogin();            }        });    }    private void windowsLogin(){        Intent i = new Intent(MainActivity.this, LoginActivity.class);        startActivity(i);    }}