package io.whatsapp.clonewhatsapp;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.github.rtoshiro.util.format.SimpleMaskFormatter;import com.github.rtoshiro.util.format.text.MaskTextWatcher;import java.util.HashMap;import io.whatsapp.clonewhatsapp.helpers.Preferencias;/** * Created by rafaelcarvalho on 18/12/2017. */public class ValidadorActivity extends AppCompatActivity {    private EditText sms;    private Button btnValidar;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_validador);        sms = (EditText) findViewById(R.id.edit_codigo);        btnValidar = (Button) findViewById(R.id.btn_validar);        SimpleMaskFormatter SimpleSMS = new SimpleMaskFormatter("NNNN");        MaskTextWatcher maskSMS = new MaskTextWatcher(sms, SimpleSMS);        sms.addTextChangedListener(maskSMS);        btnValidar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Preferencias preferencias = new Preferencias(ValidadorActivity.this);                HashMap<String, String> user = preferencias.getDadosUsuario();                String tokenGerado = user.get("token");                String tokenDigitado = sms.getText().toString();                if(tokenDigitado.equals(tokenGerado)){                    Toast.makeText(ValidadorActivity.this, "Token Validado", Toast.LENGTH_LONG).show();                }else{                    Toast.makeText(ValidadorActivity.this, "Token n√£o Validado", Toast.LENGTH_LONG).show();                }            }        });    }}