package io.whatsapp.clonewhatsapp.adapter;import android.content.Context;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import java.util.ArrayList;import io.whatsapp.clonewhatsapp.model.Contato;/** * Created by rafaelcarvalho on 20/12/2017. */public class ContatoAdapter extends ArrayAdapter<Contato> {    private ArrayList<Contato> contatos;    private Context context;    public ContatoAdapter(@NonNull Context c, @NonNull ArrayList<Contato> objects) {        super(c, 0, objects);        this.contatos = objects;        this.context = c;    }    @Override    public View getView(int position, @Nullable View convertView, @NonNull ViewGroup parent) {        View v = null;        if(contatos != null ){            LayoutInflater inflater = (LayoutInflater) context.getSystemService(context.LAYOUT_INFLATER_SERVICE);        }        return v;    }}