package io.whatsapp.clonewhatsapp.adapter;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import io.whatsapp.clonewhatsapp.fragment.ContatosFragment;import io.whatsapp.clonewhatsapp.fragment.ConversaFragment;/** * Created by rafaelcarvalho on 20/12/2017. */public class TabAdapter extends FragmentStatePagerAdapter {    private String[] tituloAbas = {"CONVERSAS","CONTATOS"};    public TabAdapter(FragmentManager fm) {        super(fm);    }    @Override    public Fragment getItem(int position) {        Fragment fragment = null;        switch (position){            case 0:                fragment = new ConversaFragment();                break;            case 1:                fragment = new ContatosFragment();                break;            default:        }        return fragment;    }    @Override    public int getCount() {        return tituloAbas.length;    }    @Override    public CharSequence getPageTitle(int position) {        return tituloAbas[ position ];    }}