package io.whatsapp.clonewhatsapp.base64;import android.util.Base64;/** * Created by rafaelcarvalho on 20/12/2017. */public class Codificacao {    public static String codificacaoBase64(String text){       return Base64.encodeToString(text.getBytes(), Base64.DEFAULT).replaceAll("(\\n|\\r)", "");    }    public static String decodificando(String senha){        return new String( Base64.decode(senha, Base64.DEFAULT) );    }}