package io.whatsapp.clonewhatsapp.configFireBase;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;/** * Created by rafaelcarvalho on 19/12/2017. */public final class ConfiguracaoFireBase{    private static DatabaseReference referenciaDataBase;    private static FirebaseAuth autenticacao;    public static DatabaseReference getFirebase(){        if(referenciaDataBase == null){            referenciaDataBase = FirebaseDatabase.getInstance().getReference();        }        return referenciaDataBase;    }    public static FirebaseAuth getFireBaseAutenticacao(){        if(autenticacao == null ){            autenticacao = FirebaseAuth.getInstance();        }        return autenticacao;    }}