package io.whatsapp.clonewhatsapp.firebase;import android.widget.Toast;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import io.whatsapp.clonewhatsapp.LoginActivity;/** * Created by rafaelcarvalho on 19/12/2017. */public final class ConfigFireBase {    private static DatabaseReference reference;    private static FirebaseAuth auth;    public static DatabaseReference getFirebase(){        if( reference == null ){            reference = FirebaseDatabase.getInstance().getReference();        }        return reference;    }    public static FirebaseAuth getFirebaseAuth(){        if( auth == null ){            auth = FirebaseAuth.getInstance();        }        return auth;    }}