package io.whatsapp.clonewhatsapp.helpers;import android.content.Context;import android.content.SharedPreferences;import java.util.HashMap;/** * Created by rafaelcarvalho on 18/12/2017. */public class Preferencias {    private Context context;    private SharedPreferences referencia;    private final String NOME_ARQUIVO = "whatsapp.preferencias";    private final int MODE = 0;    private SharedPreferences.Editor editor;    private final String CHAVE_NOME = "nome";    private final String CHAVE_TELEFONE = "telefone";    private final String CHAVE_TOKEN = "token";    public Preferencias(Context _context){        context = _context;        referencia = context.getSharedPreferences(NOME_ARQUIVO, 0);        editor = referencia.edit();    }    public void salvarUsuario(String nome, String telefone, String token){        editor.putString(CHAVE_NOME, nome);        editor.putString(CHAVE_TELEFONE, telefone);        editor.putString(CHAVE_TOKEN, token);        editor.commit();    }    public HashMap<String, String> getDadosUsuario(){        HashMap<String, String> dadosUsuario = new HashMap<>();        dadosUsuario.put(CHAVE_NOME, referencia.getString(CHAVE_NOME, null));        dadosUsuario.put(CHAVE_TELEFONE, referencia.getString(CHAVE_TELEFONE, null));        dadosUsuario.put(CHAVE_TOKEN, referencia.getString(CHAVE_TOKEN, null));        return dadosUsuario;    }}