package io.whatsapp.clonewhatsapp.model;/** * Created by rafaelcarvalho on 21/12/2017. */public class Mensagem {    private String uidUsuario;    private String mensagem;    public Mensagem() {    }    public String getUidUsuario() {        return uidUsuario;    }    public void setUidUsuario(String uidUsuario) {        this.uidUsuario = uidUsuario;    }    public String getMensagem() {        return mensagem;    }    public void setMensagem(String mensagem) {        this.mensagem = mensagem;    }}